DROP TABLE IF EXISTS `springmenu`;
CREATE TABLE `springmenu` (
  `menuId` int(20) NOT NULL AUTO_INCREMENT,
  `menuName` varchar(40) CHARACTER SET utf8 NOT NULL,
  `menuMaterial` varchar(300) CHARACTER SET utf8 NOT NULL,
  `menuContent` varchar(1500) CHARACTER SET utf8 NOT NULL,
  `pic` varchar(60) NOT NULL,
  `userId` int(20) DEFAULT NULL,
  `type` int(10) DEFAULT NULL,
  PRIMARY KEY (`menuId`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=latin1;

-- ----------------------------
-- Records of springmenu
-- ----------------------------
INSERT INTO `springmenu` VALUES ('1', '韭菜猪红汤', '主料:韭菜2两 调料:食盐适量,姜1块,调和油适量,胡椒粉适量', '1.韭菜摘洗干净后，切成寸段备用。2.枸杞洗净，沥水备用。3.猪血洗净后，切成小块备用。4.起锅烧适量清水，加入盐、姜片。5.加入猪血，大火烧开后转中火煮2分钟。6.2分钟后加入韭菜，大火煮沸后再煮1分钟左右。7.调入胡椒粉和味精，拌匀煮沸。8.加入枸杞，淋入香油拌匀即可。9.盛出装入容器中。', '/springimages/img1.png', null, '1');
INSERT INTO `springmenu` VALUES ('2', '苦瓜牛肉', '主料:苦瓜1个,牛肉100克 辅料:酱油1勺,盐适量', '1.苦瓜中间剖开，去籽后切片，里面白色瓤的部分去不去掉都可以，去掉可以少一点苦涩感。2.苦瓜切片后放入碗中，撒盐，杀一下苦水，挤一下苦水之后需要把水倒掉，不然菜品会有苦涩感。3.肉切小细丝~我家吃肉除了红烧肉在基本上只吃牛肉，牛肉吃比较不容易胖，相对于健康。4.铁锅烧热，加植物油，基本上家中炒菜的油就可以牛肉丝下锅，稍微成型后翻炒，避免肉不成熟导致粘锅，肉断裂成碎末。5.肉丝翻炒几下，变白之后，就可以加一点酱油，肉丝比较细比较容易成熟，翻炒几下就可以了。6.把苦瓜片倒入锅中，和锅中的酱油一起翻炒均匀，苦瓜稍微成熟后就可以装盘出锅啦。\r', '/springimages/img2.png', null, '1');
INSERT INTO `springmenu` VALUES ('3', '玉米排骨莲藕汤', '主料:排骨300克,莲藕1节,甜玉米1根 辅料:生姜1块,盐适量,小葱1根,米酒汁1小勺,开水适量', '1.莲藕去皮切块。2.用盐腌制10分钟，玉米洗净切成小段。3.排骨冷水入锅，放入姜片，1勺料酒，水煮开后捞出。4.将排骨，姜片，葱段放入锅中。5.加入清水，大火煮开。6.煮开后放入莲藕和玉米，小火炖一小时。7.出锅前加入适量的盐即可。', '/springimages/img3.png', null, '1');
INSERT INTO `springmenu` VALUES ('4', '韭菜炒春笋', '主料:韭菜适量,春笋适量 辅料:生抽适量,盐适量', '1.将竹笋剥好洗净。2.将竹笋切丝备用。3.将竹笋倒入开水锅中焯一下，要焯至7-8分熟，捞出备用。4.将小米椒洗净切好备用。5.将韭菜洗净切段备用。6.将锅洗净烧干，倒入适量油烧热。7.将小米椒和笋子倒入锅中翻炒片刻。8.将韭菜倒入锅中，快速翻炒至断生。9.加入适量的盐，翻炒均匀入味即可。10.装盘。\r', '/springimages/img4.png', null, '1');
INSERT INTO `springmenu` VALUES ('5', '韭菜肉松春卷', '主料:猪肉100克,韭菜100克,春卷皮12张,金针菇60克 辅料:黄豆酱半茶匙,盐1克,糖2克,生姜3克,生粉5克,植物油适量', '1.韭菜、金针菇切长段，肉、姜切丝备用。2.将肉丝用黄豆酱、糖、盐、生粉和生姜拌匀。同时备好生粉芡。3.同时备好生粉芡。4.热锅热油将肉丝滑散变色。5.倒入韭菜快速翻炒两下。6.勾入生粉薄芡翻匀。7.关火，倒入金针菇翻匀。8.盛到大盘子里，拿到外面吹风让其快速冷确。9.待肉丝凉透包入春卷皮内，用面桨封口。10.一个个地包好备用。11.热锅热油，将包好的春卷生坯一个个地放进，炸成金黄。12.捞出春卷放到厨房纸上吸去多余的油脂。13.配上香醋上桌。', '/springimages/img5.png', null, '1');
INSERT INTO `springmenu` VALUES ('6', '梅菜干扣肉', '主料:五花肉360克,梅干菜90克 辅料:老抽8克水（焖梅干菜）90克水,（煮五花肉）1200克,食用油（炸五花肉）1500克,生抽12克,料酒10克,腐乳8克,桂皮2克,八角1个,香叶2片,蒜瓣3个,盐2克', '1.五花肉放在锅中加入清水，煮二十分钟捞出，再肉皮上涂抹上蜂蜜。2.烧热锅加入食用油，肉皮朝下放入油锅中，炸至金黄色后捞出，稍微冷却后切片。3.整齐的放入碗中，留两片放入侧边，梅干菜浸泡后流水洗净。4.另起锅加入姜片，葱段，桂皮，八角，煸炒出香味。5.加入沥干的梅干菜，盐，白糖，生抽，老抽，煸炒入味后加少量水，小火煮十五分钟入味。6.调出大料，装在肉上，入笼蒸一个小时。', '/springimages/img6.png', null, '1');
INSERT INTO `springmenu` VALUES ('7', '春菜排骨煲', '主料:猪大排300g,莴笋500g 调料:食盐适量,味精适量,鸡粉适量,调和油适量,鱼露适量', '1.排骨用凉水泡3个钟头，钟头换过水。2.置锅火上加入水；倒入排骨，大火烧到气阀出气转小火15分钟。3.春菜洗净切段，菜叶菜梗分别置放。4.置锅火上加入油，烧热后放入菜梗略炒。5.加入鱼露调味，炒多1-2分钟后关火。6.将炒好的菜梗倒入排骨汤中，大火烧沸10分钟。7.加入菜叶。8.加入盐调味。9.加入鸡粉煮10分钟即可出锅。', '/springimages/img7.png', null, '1');
INSERT INTO `springmenu` VALUES ('8', '春笋炒虾滑', '主料:虾滑一包,芦笋八根 辅料:盐适量,姜胡椒粉适量,料酒适量', '1.洗净芦笋。2.芦笋去掉根部刨皮。3.芦笋切斜段，等焯水。4.烧开水，放点盐，放入芦笋焯水一二分钟，捞出放入凉水中备用。5.虾滑解冻好备用。6.把解冻的虾滑，用刀切成一小块一小块。7.热锅放油小火煮虾滑变色盛出备用。8.热锅倒油，放虾滑芦笋，倒入料酒翻炒一会，依个人口味放盐胡椒调味即可。', '/springimages/img8.png', null, '1');
INSERT INTO `springmenu` VALUES ('9', '雪菜肉末炒四季豆', '主料:肉末1小碗,雪菜1小碗四季豆1把 辅料:料酒1勺,生抽1勺,油盐适量', '1.准备好原材料。2.烧锅开水， 加入几滴油和少许盐， 将四季豆放入里面焯煮到九成熟。3.焯好的四季豆切成细粒。4.热锅凉油， 放入肉末进去翻炒。5.翻炒至肉末变色后烹入料酒和生抽， 继续翻炒。6.放入雪菜进去翻炒。7.翻炒几下后就加入四季豆末进去翻炒。8.最后加入适量的盐调味， 盛入盘中。', '/springimages/img9.png', null, '1');
INSERT INTO `springmenu` VALUES ('10', '银耳莲子雪梨', '主料:莲子50克,雪梨1个,银耳100克 辅料:冰糖适量,枸杞子适量', '1.银耳泡发后去掉根部发硬的部分，莲子浸泡两个小时后去莲心。2.梨去皮。3.将梨切成块。4.银耳撕成小朵后和莲子一起放入电压力锅内胆，加入水。5.放入梨。6.加入冰糖。7.电压力锅接通电源，选择靓汤程序，程序结束后自动泄压，开盖就可以吃了。', '/springimages/img10.png', null, '1');
INSERT INTO `springmenu` VALUES ('11', '淮山胡萝卜鲫鱼汤', '主料:1.鲜淮山400克，胡萝卜400克，瘦肉150克，鲫鱼500克（1-2条），蜜枣2粒，姜3片。辅料：食盐适量,姜3片', '1.淮山去皮切厚块，胡萝卜去皮切厚块，蜜枣，姜片，瘦肉洗净。2.胡萝卜，淮山，瘦肉，姜片，蜜枣放入汤锅加2500毫升水 大火煲着。3.先煲汤其他材料 ，我们来准备煎鱼，煲汤的鲫鱼我一般选择不去鱼鳞，因为鱼鳞也是很有营养的。鱼洗净，热油锅放适量盐姜片爆下，放鲫鱼下去煎 2面都煎黄。4.开大火加入一碗清水煮开。5.汤锅里也大火煲开了，把煎黄的鲫鱼铲过去 放进汤锅里。6.连同那煎锅里那汤汁也倒进汤锅里大火煲3-5分钟，顺便把浮沫撇去这样汤会更清不会那么油腻，因为煎鱼放了油，这时候都会浮在刚煲开的汤面，很容易撇去。7.改小火1个半小时。8.放盐调味即可享用。', '/springimages/img11.png', null, '1');
INSERT INTO `springmenu` VALUES ('12', '白灼菜心', '主料:油菜苔400克 辅料:食油3汤匙,食盐1茶匙,大蒜3瓣,生抽3汤匙,蚝油2汤匙', '1.把菜心去除老叶，用刀把菜心一分为二。2.菜心洗净控水，大蒜切粒。3.水中放入适量油烧开，手提菜心叶子把梗先放入锅中，煮至快熟时全部放入煮至叶子变软即。4.把菜心捞出控水装盘。5.起油锅爆香大蒜。6.加入适量盐。7.调入适量生抽和蚝油搅匀。8.最后把做好的蚝油蒜汁淋入菜心上即可。', '/springimages/img12.png', null, '1');
INSERT INTO `springmenu` VALUES ('13', '芹菜炒牛肉', '主料:牛肉180克,芹菜芯150克,姜一块,蒜少许 辅料:油20毫升,盐3克,蚝油10克,生粉少许,味精少许', '1.牛肉切丝。2.加盐，五香粉，淀粉，蛋清。3.腌5分钟再加少许植物油搅匀。4.芹菜切段。5.炒锅中放植物油，烧热，放牛肉炒散，变色后盛出。6.余油炒芹菜。7.放回肉丝。8.炒匀，放盐炒匀，放鸡精炒匀即可。', '/springimages/img13.png', null, '1');
INSERT INTO `springmenu` VALUES ('14', '粉丝娃娃菜', '主料:粉丝,娃娃菜,小米椒,蒜,葱 辅料:花椒,八角,蒜蓉辣椒酱,盐', '1.粉丝用温水泡开。2.食材切好备用。3.粉丝控干水分。4.娃娃菜焯水。5.摆盘。\r6.锅内热油炒香八角花椒。7.炒香蒜末辣椒。8.倒入蒜蓉辣椒酱翻炒均匀。9.把炒好的汁淋在粉丝上。10.水开蒸10分钟。', '/springimages/img14.png', null, '1');
INSERT INTO `springmenu` VALUES ('15', '韭菜炒鱿鱼', '主料:韭菜300克,鱿鱼1个 辅料:花生油25克,精盐4克,味精2克', '1.鱿鱼洗净，去膜（黑色的脏东西都不要）去骨头。2.切菱形格子。3.再切小块。4.热水烧开，放入鱿鱼，倒适量的料酒去腥味。5.捞出，清水洗净。6.韭菜洗净切段。大蒜切末，生姜切片，大蒜切末，鱿鱼洗净。7.热锅放油，放入大蒜煸香。8.放入韭菜爆炒。9.倒入鱿鱼爆炒。10.放入小米椒，生姜。11.适量的鸡精，盐巴。12.料酒，翻炒均匀，出锅。', '/springimages/img15.png', null, '1');
INSERT INTO `springmenu` VALUES ('16', '香菇手撕包菜', '主料:绿甘蓝半棵,鲜香菇3个,红椒半个 辅料:蒜子4瓣,香油1勺,食用油适量,盐适量,鸡精适量', '1.将甘蓝菜用手撕成小块，清水冲洗干净，晃动沥水菜篮，让水沥干净。2.香菇洗净去蒂切片，蒜切末、红椒切成三角形。3.锅烧热放食用油，大火，油热放入蒜末、香菇煸炒1分钟。4.然后倒入手撕菜叶和红椒，翻炒。5.炒制菜叶断生，放入食盐、鸡精、香油，关火翻炒均匀出锅。6.脆爽的香菇手撕包菜完成，装盘开饭。', '/springimages/img16.png', null, '1');
INSERT INTO `springmenu` VALUES ('17', '醋溜白菜', '主料:白菜适量 辅料:食用油5毫升,花椒少许,蒜片适量,干辣椒丝适量,生抽5克,陈醋5克,盐3克', '1.锅中放油，倒入花椒、蒜片、干辣椒丝，大火爆香。2.倒入白菜。3.倒入生抽、陈醋、盐，大火爆炒2分钟。4.出锅即可食用。', '/springimages/img17.png', null, '1');
INSERT INTO `springmenu` VALUES ('18', '清炒藕丁', '主料：藕300克，青椒80克 辅料:盐10克，生抽10毫，升香油5毫升，老抽5毫升', '1.材料准备好，洗净。2.藕去皮。3.藕去皮后，改成藕丁状。4.藕切成丁后，用清水泡一下，放一点白醋，可以让藕丁不会变黑。5.藕丁泡一下水后，捞出沥干水，在放入清水中煮，煮熟。6.煮熟后的藕放入冷水中，冲冷，沥干水备用。7.热锅冷油，下入青椒，炒至微熟。8.把青椒炒至微熟，下入藕丁，慢慢炒。9.用一个小碗，碗里放入生抽、盐、生粉、老抽、香油；在加入一点水，勾况欠汁。\r10.等藕丁炒几分钟后，下入欠汁，小火，慢慢收汁，让藕丁上色。11.藕丁上色后，用小碗装出，就可以食用了。', '/springimages/img18.png', null, '1');
INSERT INTO `springmenu` VALUES ('19', '薏米排骨汤', '主料：猪大排1碗，薏米1把 调料：食盐适量，江米酒适量，水适量，白糖适量', '1.薏米淘洗干净浸泡1小时左右.2.排骨加水煮开后稍煮几分钟。3.用温水冲洗干净，加温水和姜片煮开。4.加酒酿后转小火炖1小时右。5.加入浸泡过的薏米和盐再炖半个小时左右，至薏米软烂。6.出锅前可以调入一些糖提鲜。', '/springimages/img19.png', null, '1');
INSERT INTO `springmenu` VALUES ('20', '春饼韭菜盒子', '主料：春饼16张，香油1勺，韭菜700克，鸡蛋4个，盐1勺，五香粉1勺，鸡粉1勺，生抽1勺，耗油1勺，菜籽油1汤勺 工具：电饼铛1个', '1.韭菜清洗干净，控水后，切碎备用。2.四个鸡蛋打入碗里搅打均匀。3.炒熟后，用锅铲捣碎备用。4.先给韭菜里倒入一汤勺菜籽油，把韭菜搅拌均匀，以免放入盐后，韭菜出水严重。5.韭菜里放入炒熟的鸡蛋。6.再放入盐，五香粉，鸡粉，生抽，耗油，香油，搅拌均匀备用。7.春饼皮放上韭菜馅。8.折叠。9.最后翻面压底。10.全部码好。11.电饼铛预热刷油，放入韭菜盒子，表面也刷一层油。12.煎2分钟，翻面。13.煎至两面金黄即可出锅食用。', '/springimages/img20.png', null, '1');
INSERT INTO `springmenu` VALUES ('21', '韭菜炒虾', '主料：河虾250克，韭菜100克 辅料：姜2片，玉米面(黄)1把，油2勺，盐1小勺', '1.烧锅加3汤勺油烧热，下入已清洗好的河虾，加1汤勺料酒。2.翻炒翻炒。3.接着，合入切好的韭菜翻炒一下。4.然后，加1汤勺生抽。5.加0.5汤勺白糖。6.加半勺盐。7.调味炒匀即成。', '/springimages/img21.png', null, '1');
INSERT INTO `springmenu` VALUES ('22', '韭菜饼', '主料：春饼2张，韭菜1小把 辅料：鸡蛋2个黑木耳2朵 调料：食盐1/2小勺，橄榄油5克，芝士片1片', '1.将切成细碎状的韭菜和木耳放入碗中，磕入一个鸡蛋，再加入橄榄油、盐，拌匀待用。2.预热好的电饼铛中放一张春饼，倒入另一个打散的鸡蛋液，用筷子划着摊均匀，然后盖上电饼铛的盖子。3.半分钟左右，当电饼铛周围冒出很多热气的时候，鸡蛋就熟了，将蛋饼取出。4.然后再放入另一张春饼，上面铺满韭菜木耳鸡蛋馅，注意不要让馅料流出饼皮。5.盖上加了鸡蛋的那张饼，最后再盖上电饼铛的盖子，2-3分钟后，鸡蛋凝固即可拿出。6.出锅后，将韭菜饼切成4块，装盘就可以趁热吃了。7.还可以再放入一片奶酪做夹层，热的饼会将奶酪捂化，吃起来更是别有一番风味。', '/springimages/img22.png', null, '1');
INSERT INTO `springmenu` VALUES ('23', '四季豆炒肉末', '主料：四季豆250克，猪肉馅60克 辅料：色拉油适量，生抽1勺，料酒1勺，酱香鲜2勺，蒜瓣3个，金格勒酱1勺，砂糖2小勺，盐少许，红辣椒2个', '1、姜、葱、蒜、辣椒切末。2、绞肉清水洗净后淋点米酒去腥，四季豆挑菜洗净切段。3、倒点油热锅后放入姜、蒜末爆香，放入猪绞肉拌炒至7分熟后，放入酱油、味霖、辣豆瓣酱拌匀。4、再放入四季豆、少许盐拌炒一下后，加点水闷煮约5~10分钟。5、待至四季豆熟后，加入辣椒、葱花拌炒一下即可起锅上菜。', '/springimages/img23.png', null, '1');
INSERT INTO `springmenu` VALUES ('24', '酸菜猪肉笋丝', '主料：春笋500克，猪肉150克，酸菜150克 辅料：油2汤匙，生粉1茶匙，盐2茶匙，黄酒1茶匙，葱花少许', '1.春笋去壳，去老根后洗净，酸菜洗净挤干水分，猪腿肉洗净。2.分别切成丝后，猪肉丝里加入生粉，少许盐，黄酒抓匀静置半小时。3.先把笋丝冷水下锅，加入1茶匙盐焯水后捞起洗净控水。4.热锅倒油，温油把猪肉丝下入炒至变色。5.再把酸菜倒入不断的煸炒出香味后全部盛起。6.锅内留底油，把笋丝倒入煸炒出香味后加入盐，酸菜和猪肉丝都有咸味的，加入少许盐让笋丝有个底味就好。7.再把酸菜肉丝回到锅中，翻炒至和笋丝融合熄火出锅。8.盛出后撒上葱花即可享用。', '/springimages/img24.png', null, '1');
INSERT INTO `springmenu` VALUES ('25', '干煎荠菜笋肉馄饨', '主料：荠菜250克，猪肉末500克，春笋200克，馄饨皮250克 辅料：油2汤匙，蚝油1汤匙，盐4克，黄酒2汤匙，鲜酱油1汤匙，鸡蛋1个，香油少许，米醋少许', '1.备好剁碎的荠菜，馄饨皮，猪肉末，春笋去壳，去老根后切长片。2.把春笋冷水下锅焯水后捞起剁碎。3.猪肉末里磕入一个鸡蛋，加盐，黄酒，和蚝油按顺时针方向搅拌至有粘性，再加入少许清水再一次搅拌至有粘性后把荠菜倒入混合均匀。4.把剁碎的笋倒入加入少许香油和鲜酱油搅拌均匀。5.取一张皮子加入馅料后包起。6.这是包好的一部分。7.不粘锅加热后倒入油。8.温油把馄饨加入用中火煎至底部变黄。9.倒入半碗清水，水量能把馄饨底部浸没为佳，盖上大火煎。\r10.一直煎到水分收干后开盖，把底部煎脆后熄火出锅。', '/springimages/img25.png', null, '1');
INSERT INTO `springmenu` VALUES ('26', '香椿炒鸡蛋', '主料：香椿100克，鸡蛋2个 辅料：植物油2汤勺，盐1茶勺', '1.把鸡蛋打进碗里，加入少许盐。2.用筷子搅打成鸡蛋液备用。3.香椿洗净后，焯水30秒至叶子变绿。4.捞出控干水分。5.切成小碎段。6.将鸡蛋打进碗里，放进香椿和盐搅拌均匀。7.锅里放入适量油烧热，均匀倒进鸡蛋糊。8.先不要翻动，待鸡蛋液稍稍定型。9.用锅铲边翻炒边碾碎成小块，蛋液全部成形就炒好了。', '/springimages/img26.png', null, '1');
INSERT INTO `springmenu` VALUES ('27', '荠菜春笋', '主料：春笋半个，荠菜300克 调料：食盐1茶匙，红尖椒2个，植物油适量，水适量', '1.准备好原材料。2.春笋切成片，放入冷水中焯一下后捞出。3.开水中加入适量的盐和几滴油。4.将荠菜放入开水中焯一下捞出，焯过的荠菜切成碎。5.烧热锅， 放入适量的油， 下入焯过水的春笋翻炒。6.翻炒几下后加入少量的水煮一下。7.水快干时加入荠菜和红椒圈翻炒。8.出锅前加入适量的盐调味，盛入盘中。', '/springimages/img27.png', null, '1');
INSERT INTO `springmenu` VALUES ('28', '东坡肉', '五花肉', '蒸煮炒', '/springimages/1585475389326.png', '3', '1');
INSERT INTO `springmenu` VALUES ('29', '1', '1', '1', '/springimages/1585555315841.png', '3', '1');
