DROP TABLE IF EXISTS `summercollection`;
CREATE TABLE `summercollection` (
  `userId` int(30) NOT NULL,
  `collectId` int(30) NOT NULL,
  PRIMARY KEY (`collectId`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

-- ----------------------------
-- Records of summercollection
-- ----------------------------

-- ----------------------------
-- Table structure for `summercomment`
-- ----------------------------
DROP TABLE IF EXISTS `summercomment`;
CREATE TABLE `summercomment` (
  `userId` int(30) NOT NULL,
  `menuId` int(30) NOT NULL,
  `comment` varchar(500) CHARACTER SET utf8 NOT NULL,
  `commentId` int(30) NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`commentId`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;

-- ----------------------------
-- Records of summercomment
-- ----------------------------

-- ----------------------------
-- Table structure for `summermenu`
-- ----------------------------
DROP TABLE IF EXISTS `summermenu`;
CREATE TABLE `summermenu` (
  `menuId` int(20) NOT NULL AUTO_INCREMENT,
  `menuName` varchar(40) CHARACTER SET utf8 NOT NULL,
  `menuMaterial` varchar(300) CHARACTER SET utf8 NOT NULL,
  `menuContent` varchar(1500) CHARACTER SET utf8 NOT NULL,
  `pic` varchar(60) NOT NULL,
  `userId` int(20) DEFAULT NULL,
  `type` int(10) DEFAULT NULL,
  PRIMARY KEY (`menuId`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=latin1;

-- ----------------------------
-- Records of summermenu
-- ----------------------------
INSERT INTO `summermenu` VALUES ('1', '夏季凉拌菜', '主料：黄瓜适量，腐竹适量，木耳适量 辅料：花生米适量，蒜末适量，香菜适量，小米辣适量，辣椒油1小碗，生抽1勺，香醋1勺，盐适量，糖适量，鸡精适量', '1.木耳、腐竹泡发后，焯水30秒后捞出。2.碗中放入腐竹、木耳、黄瓜、花生米、蒜末、香菜、小米辣。3.加一勺生抽、一勺香醋，少量盐、糖、鸡精，适量辣椒油，搅拌均匀即可。', '/summerimages/img1.png', null, '2');
INSERT INTO `summermenu` VALUES ('2', '苦瓜排骨汤', '主料：苦瓜2根，排骨300克 辅料：葱段8克，料酒10克，鸡粉3克，盐2克', '1.苦瓜洗干净。2.将苦瓜一分为二。3.用勺子将苦瓜的籽和内瓤挖干净。4.然后将苦瓜切成段。5.锅中放入水，加入姜片和料酒，放入排骨焯水。6.焯好水的排骨重新干净浮沫。7.将排骨放入锅内，加入水，放入生姜。8.加入料酒。9.大火煮开后，转小火炖一小时至排骨软烂。10.放入苦瓜段继续煮二十分钟。11.加入盐调味。12.加入鸡精13.放入一点葱花就可以关火开吃。', '/summerimages/img2.png', null, '2');
INSERT INTO `summermenu` VALUES ('3', '苦瓜炒蛋', '主料：苦瓜1根，鸡蛋2个 辅料：大蒜适量，辣椒2根，食用油5毫升，生抽3克，料酒3克，盐3克', '1.苦瓜对半剖开，去掉白瓤，洗净后，切成苦瓜片。2.3个鸡蛋磕进碗里，调入食盐。3.调入料酒和温水，将蛋液搅打均匀。4.锅置火上，烧热后倒入适量植物油，油锅烧热后，转小火，将蛋液倒入锅里。5.蛋液炒好后，捣散后，盛出。6.炒锅再倒入植物油烧热后，放入花椒煸香。7.放入苦瓜片大火翻炒。8.调入生抽。9.放入鸡蛋块翻炒。10.食材都炒熟后，调入食盐。11.翻炒均匀，关火。12.出锅，装盘。', '/summerimages/img3.png', null, '2');
INSERT INTO `summermenu` VALUES ('4', '苦瓜炒虾仁', '主料：苦瓜1根，虾6只 辅料：植物油15毫升，蚝油半勺，蒜2瓣，盐适量', '1.苦瓜清洗干净，一开两半，掏去瓜籽。2.斜刀切成片。3.大蒜去皮后用刀拍松，然后切末。4.鲜虾去皮后，开背挑去虾线。5.锅内烧开水，放入处理好的虾仁，虾体卷起颜色变红捞出(煮虾后的水可以做汤，很鲜美哦)。6.炒锅放油烧热，爆香蒜末。7.蒜末微微变黄后倒入切好的苦瓜。8.紧接着倒入虾仁，迅速调味关火，非常的脆嫩鲜美。', '/summerimages/img4.png', null, '2');
INSERT INTO `summermenu` VALUES ('5', '耗油生菜', '主料：生菜100克 辅料：蚝油5克，大蒜1瓣，食用油5毫升，白砂糖5克', '1.食材准备:生菜、青葱洗净备用，姜、蒜剁碎备用。2.调汁:取一小碗，倒入适量生抽、耗油、食盐调好备用。3.焯水:生菜在滚水中烫熟，捞出摆盘。4.将调好的酱汁淋在生菜上。5.然后依次铺上蒜、姜蓉、红辣椒、熟白芝麻，最后泼上热油即可。', '/summerimages/img5.png', null, '2');
INSERT INTO `summermenu` VALUES ('6', '蒸豆角', '主料：老豆角350克，面粉100克 辅料：大蒜3瓣，醋1勺，生抽2勺，盐适量，香油1茶匙', '1.将豆角切段，磕入鸡蛋，倒入面粉、玉米粉和少许盐搅拌均匀。2.倒入蒸锅大火蒸10分钟即可。3.将蒸好的豆角倒入盆中。4.碗中放入小米辣、白芝麻、蒜泥淋上少许热油，再倒入1勺生抽、1勺醋、少许盐、少许糖、几滴芝麻油搅拌均匀。5.倒入豆角中搅拌均匀即可。', '/summerimages/img6.png', null, '2');
INSERT INTO `summermenu` VALUES ('7', '夏日白切鸡', '主料：鸡2腿 辅料：姜一块，葱2条，蒜5瓣，水2L', '1.将姜洗净，切丝。葱切成2半，葱白留着，葱叶切丁。2.把水煮开后放入葱白，姜。3.把鸡肉放入滚水里煮7分钟。4.再焖20分钟。5.在焖鸡的时候把蒜葱切丁。6.热锅起油后把蒜葱爆香，后加入酱油。7.把鸡捞起后放凉切块。', '/summerimages/img7.png', null, '2');
INSERT INTO `summermenu` VALUES ('8', '苦瓜酿肉', '主料：苦瓜1根，猪肉(瘦)100克 辅料：香油5克，食盐5克，豆瓣酱8克，鸡蛋1个，胡椒粉1克，姜1块，蒜1瓣，小葱1棵，淀粉5克，水淀粉1小勺，番茄酱5克', '1.准备好食材。2.苦瓜洗净切段挖去中间白色的瓤。3.苦瓜去除瓤后放入盘中，撒上盐进行腌制后用水冲洗干净。4.猪肉切成末，加入盐、淀粉和酱油搅拌均匀，再撒上葱花拌匀备用。5.用筷子将肉末酿入苦瓜中。6.将苦瓜依次酿入肉末放入盘中。7.大火煮开后转中小火蒸10分钟。', '/summerimages/img8.png', null, '2');
INSERT INTO `summermenu` VALUES ('9', '酸辣凉面', '主料：黄瓜两根，熟面条300克 辅料：花生油40克盐5克生抽20克老干妈辣椒酱两勺凉拌醋1勺十三香少许', '1.鲜虾仁用盐腌制10分钟，西红柿切丁、黄瓜切丝、香菜切碎、辣椒切成圈，黄豆芽摘除根部备用。2.锅内烧开水，将生面条放入锅内煮至水沸腾，再加入半碗凉水，如此反复两次直至面条煮熟。3.煮好的面条用凉水冲冷后，浸入冰开水中约5分钟，捞起沥干水份。4.煮面条的开水，可以放入虾仁及黄豆芽氽烫至熟，同样浸入冰开水中过凉，捞起沥干水份。5.生抽、陈醋、白糖、番茄酱、柠檬汁、凉开水(3大匙)、辣椒油、芝麻油、蒜蓉、辣椒圈、香菜碎放在碗内调匀即成凉面料汁。\r6.面条放入碗内，表面码上熟虾仁、黄瓜丝、西红柿丁、黄豆芽，再淋上凉面酱汁即。', '/summerimages/img9.png', null, '2');
INSERT INTO `summermenu` VALUES ('10', '沙拉意面', '主料：意大利面50克，胡萝卜适量，小番茄5个，虾适量，豇豆适量，腰果适量 辅料：鱼露10克，小米辣适量，柠檬汁10克，黑胡椒适量蜂蜜or棕榈糖10克，，植物油适量，大蒜适量，盐适量，白葡萄酒适量', '1.锅里烧开水，放进去一点盐，煮意面8分钟后捞出，煮土豆和豆角至熟软捞出，最后把鸡蛋扔进去煮；这时准备不需要煮的东西：广味腊肠和洋葱分别切成小块。2.煮好的鸡蛋用分蛋器切开。3.最后把所有的材料混合，放沙拉酱。', '/summerimages/img10.png', null, '2');
INSERT INTO `summermenu` VALUES ('11', '冬瓜排骨汤', '主料:排骨160克，料酒2大勺，冬瓜250克 辅料:水1000毫升,盐2小勺,味精1/4小勺,生姜片3片', '1.倒入排骨，清水煮沸。2.加黄酒，排骨去腥去血沫。3.放入姜和排骨，加1.5升水，中火煮30分钟。4.放入冬瓜，小火煮10分钟。5.放入盐、鸡粉，搅拌均匀，盛出即可，撒上葱花。', '/summerimages/img11.png', null, '2');
INSERT INTO `summermenu` VALUES ('12', '皮蛋拌黄瓜', '主料：黄瓜1根，皮蛋3个 辅料：盐2/3匙，鸡精1匙，酱油0.5勺，陈醋1勺，香油1匙，蒜2', '1.黄瓜拍裂，切块。2.皮蛋去壳切块。3.加入盐，鸡精，酱油，陈醋，香油和蒜末。4.轻轻从下向上翻拌匀即可。', '/summerimages/img12.png', null, '2');
INSERT INTO `summermenu` VALUES ('13', '青椒肉丝', '主料：里脊200克，青椒2个，红椒2/1个，姜5克，蒜2瓣 辅料：生抽1茶匙，白糖2/1茶匙，料酒1茶匙，盐少许', '1.材料准备好；里脊肉洗净，顺着纹理切丝。2.调入生抽、白糖、淀粉，用手抓匀，腌制10分钟。3.青红椒切丝，姜蒜切沫，锅里热油，放入青红椒拌炒。4.炒至青红椒7、8层熟后盛出，锅里留少许油，接着放入肉丝，大火快速炒散。5.炒至肉丝变色后放入姜沫同炒，接着放入青红椒丝，炒匀。6.放入蒜蓉，调入少许盐，拌炒均匀即可。', '/summerimages/img13.png', null, '2');
INSERT INTO `summermenu` VALUES ('14', '鲫鱼汤', '主料：大葱3克，生姜3克，鲫鱼1条，淀粉10克，八角1个，葱段适量，水1升 辅料：食用油5毫升，盐2克', '1.准备大葱3g切片，生姜3g切片、鲫鱼1条去头去鳍。2.鲫鱼放入盘中，两面均匀撒上淀粉10g。3.起锅倒入油5ml，倒入姜片并放入鲫鱼，煎至两面金黄。4.起锅倒入水1L，水中放入八角1个、葱段和鲫鱼，大火煮沸。5.加入盐2g，中火煮10分钟即可。', '/summerimages/img14.png', null, '2');
INSERT INTO `summermenu` VALUES ('15', '凉拌莴笋', '主料：莴笋,胡萝卜 辅料:花椒,盐辣椒,生抽1勺,白糖,白醋1.5勺', '1.莴笋1根去皮。2.切条。3.加入盐腌一会。4.腌好的加入胡萝卜丝。5.加入花生米。6.加入生抽。7.加入香醋。8.搅拌好。9.装盘。\r', '/summerimages/img15.png', null, '2');
INSERT INTO `summermenu` VALUES ('16', '青瓜炒肉', '主料:黄瓜100克,猪肉100克 辅料:蒜4瓣,淀粉2茶,匙鸡粉1茶匙,食盐1茶匙,植物油2汤匙,料酒1/2茶匙', '1.洗好青瓜。2.切肉片。3.横着切。4.放油炒。5.放盐，耗油，鸡精一起翻炒。6.加些油，和生抽，即可。\r', '/summerimages/img16.png', null, '2');
INSERT INTO `summermenu` VALUES ('17', '青瓜炒牛肉', '主料:青瓜一条,牛肉180,姜一块,蒜半个,泡椒半个 辅料:油30毫升,盐少许,蚝油10毫升', '1.准备好牛肉用盐糖腌制好，青瓜，姜蒜苗。2.锅里下油爆香，把牛肉放入爆炒。3.爆炒一下后下入酱油翻炒。4.把青瓜放入炒。5.最后放入蚝油搅拌均匀即可。', '/summerimages/img17.png', null, '2');
INSERT INTO `summermenu` VALUES ('18', '丝瓜虾仁', '主料:丝瓜1只,虾仁适量 辅料:蒜米适量,生抽15克,淀粉5克,清水15克,食用油10克,盐2克', '1.将丝瓜去除表皮，然后切成滚刀块。2.新鲜海虾去除虾头、虾壳和虾线，姜和蒜切好备用。3.炒锅中放入适量调和油，油热后将大蒜和姜丝放入锅中爆出味。4.将虾仁放入锅中。5.炒至变色出香味。6.然后将丝瓜块放入锅中。7.翻炒一会至丝瓜变软。8.淀粉中放入少许盐，再放入水调匀，然后倒入锅中勾芡。9.将勾芡的汁收浓一些即可。10.将炒好的丝瓜虾仁盛入盘中，美味爽口顺滑的一道家常菜就做好了。', '/summerimages/img18.png', null, '2');
INSERT INTO `summermenu` VALUES ('19', '酸辣土豆丝', '主料:土豆3个 辅料:植物油适量,盐少许,米醋适量,辣椒面1小勺', '1.3个土豆削皮，洗净。2.用擦丝器将土豆擦成粗丝。3.用凉水清洗两遍。4.沥干水，再晾5分钟左右，将表面的水能充分吸进土豆丝中。5.辣椒面准备好，一会儿炒的时候可以根据家人食辣的程度及辣椒面的辣度来决定加多少。6.炒锅中多放一点油，这样可以防止炒的时候粘锅；油温热时倒入一点点辣椒粉，炝出香味来。7.待辣椒粉变成褐色时，快速将土豆丝倒入锅中翻炒，待土豆丝变得有点透明时，倒入米醋，如果不嫌颜色深，倒入陈醋、香醋也是可以的。8.翻炒均匀，再撒入适量的辣椒粉，这样颜色和味道都有了进一步提升。', '/summerimages/img19.png', null, '2');
INSERT INTO `summermenu` VALUES ('20', '红烧豆腐', '主料:南豆腐一块,红烧排骨酱半袋 辅料:油一勺,盐一撮,麻椒碎10克,豆豉15克,姜蒜末8克,酱油2勺,水淀粉50克\r', '1.豆腐切成1.5厘米左右的正方形小块，葱姜蒜都切成丁。2.烧开一锅水，将豆腐丁倒进去汆烫。3.捞出，沥干水。4.油锅烧至五成热，放入红烧排骨酱和豆豉，煸炒出香味。5.然后放入姜末、蒜末。6.加入半碗清水、酱油。7.加入盐调味。\r8.烧开后放入豆腐、大火烧煮一分钟，为了避免糊锅，中间适度旋转锅身，取一半水淀粉勾芡，继续旋动锅身烧煮大约一分钟，再放剩下的水淀粉。9.撒上麻椒碎末和葱花即可。10.香辣入味的红烧豆腐出锅。', '/summerimages/img20.png', null, '2');
INSERT INTO `summermenu` VALUES ('21', '丝瓜豆腐汤', '主料:丝瓜1根,北豆腐400克,胡萝卜1根,木耳(水发)70克 辅料:鸡骨500克,盐适量,葱1棵,姜3片,料酒1汤匙', '1.葱切段、姜切片。2.鸡骨冷水入锅煮开焯水捞出。3.焯好水的鸡骨入开水锅加入葱段、姜片、料酒大火煮开转中小火煲30分钟。4.木耳提前泡发洗净泥沙。5.丝瓜去皮、胡萝卜去皮。6.豆腐切成小块。7.胡萝卜斜切成段。8.再切成象眼块。9.丝瓜切滚刀块。10.煲好的鸡汤倒入砂锅。11.先下入豆腐煮10分钟。12.再下入丝瓜煮开。13.丝瓜变软后加入木耳、调入盐继续煮开。14.最后下入胡萝卜片煮开即可出锅。', '/summerimages/img21.png', null, '2');
INSERT INTO `summermenu` VALUES ('22', '酸汤肥牛', '主料:金针菇300克,肥牛250克 辅料:姜适量,蒜适量,黄灯笼辣椒酱适量,米醋50毫升,白砂糖适量,盐适量,泡椒适量', '1.酸菜洗净切片，香葱切成2cm的段，蒜切片，尖椒切成圈。2.肥牛从冰箱冷冻室内取出后，稍微放在室温环境内缓一下，之后上面撒上生粉，用手抓匀。3.大火烧开锅中的水，转小火，将肥牛放入水中，汆烫断生后立即捞出，控干备用。4.锅中放15ml油，烧到5成热，放入姜、蒜、野山椒，煸出香味后放入番茄酱划炒开，稍微变色后，加入酸菜炒出香味。5.加入水烧开后加入各种调味料。6.再次烧开后，捞出酸菜，放入盛盘中垫底。将汆烫过的肥牛放入锅中，烧开后立即关火，连汤一起倒入碗中的酸菜上面。7.红尖椒圈和香葱段淋上10ml热油，捞出，放在做好的酸汤肥牛上即可。', '/summerimages/img22.png', null, '2');
INSERT INTO `summermenu` VALUES ('23', '酸菜鱼', '主料:蛋清1个,草鱼片500克 辅料:淀粉5克,油8毫升,姜丝适量,葱适量,酸菜500克,鸡粉3克,老抽5克,胡椒粉1克', '1.碗内倒入鱼肉 盐 胡椒粉 料酒。2.抓匀。3.倒入鸡蛋清和淀粉,抓匀。4.锅底烧油倒入鱼骨,煎香后捞出。5.倒入酸菜,鱼骨, 酸菜鱼调料,搅拌均匀。6.加水没过食材。\r7.煮沸捞出鱼骨酸菜。8.倒入鱼片。9.炒熟捞出。10.淋上辣椒油完成。', '/summerimages/img23.png', null, '2');
INSERT INTO `summermenu` VALUES ('24', '四季豆炒茄子', '主料:四季豆400克,茄子1根 辅料:花生油10毫升,盐4克,酱油2毫升,青椒2个,干辣椒5个,香葱20克', '1.茄子洗净切块，清水浸泡3分钟，捞出沥干水。2.四季豆洗净去豆荚。3.热锅，倒油，放入茄子、四季豆一起炸熟，捞出沥干油。4.把多余的油倒出来，锅中留小许有，放入辣椒、葱头爆炒香。5.沥干油的茄子、四季豆倒入翻炒。6.放酱油、盐、香葱炒均收汁即可。', '/summerimages/img24.png', null, '2');
INSERT INTO `summermenu` VALUES ('25', '菠萝炒饭', '主料:米饭1大碗,菠萝1个,鸡蛋1个,豌豆25克,火腿25克,红椒15克,虾5只,腰果15克,葡萄干10克 辅料:酱油1汤匙,盐适量,油适量', '1.菠萝在三分之二处切开，挖出果肉，做成菠萝碗;取适量果肉切小丁。2.豌豆焯熟；火腿切小丁，红椒切碎；虾取虾仁去虾线。3.热油锅，打入鸡蛋炒碎盛出。\r4.把腰果和葡萄干稍炒一下。5.锅中再加点油，倒入火腿丁、虾仁炒一会儿后加豌豆、红椒炒匀。6.倒入米饭，搅炒到完全散开。7.加入鸡蛋、腰果和葡萄干翻炒均匀。8.加盐、酱油调味，再加入菠萝丁炒均匀就可以了。\r\r', '/summerimages/img25.png', null, '2');
INSERT INTO `summermenu` VALUES ('26', '糖醋排骨', '主料:排骨500克,蒜适量 辅料:甜辣酱30克,料酒10克,香油5克,生抽10克,白醋15克,香炸粉25克,糖5克,老抽5克,香叶3片,八角3个', '1.排骨清洗干净。2.入凉水锅中煮开，然后夹出备用。3.半棵葱切段、姜切片、八角一颗。4.与排骨同入砂锅中，小火焖煮，煮到排骨十分熟。5.排骨捞出做糖醋排骨用，剩下的汤煮汤做面都可以，不要浪费。6.盐1勺，白糖3勺，淀粉1勺，酱油适量，醋适量，一小碗凉水能没过排骨量就成，倒入锅中煮开。7.将排骨入锅中，盖盖子，小火慢炖5-10分钟，直到汤汁浓稠；临出锅前再淋少许醋，糖醋味才浓郁。8.出锅后撒香葱末或者白芝麻均可。', '/summerimages/img26.png', null, '2');
INSERT INTO `summermenu` VALUES ('27', '香辣花甲', '主料:花甲1000克 辅料:植物油100克,老姜1块,大蒜20瓣,小米辣20个,小葱2颗,香菜2颗,料酒1大勺,生抽2大勺,蒸鱼豉油2大勺,豆瓣酱1大勺,啤酒2大勺', '1.扇贝刷洗干净后，放入锅里焯水。2.扇贝开壳后，关火捞出。3.老姜切丝；小葱、香菜切断；小米辣切圈；大蒜切粒。4.炒锅烧热，放入植物油烧热；放入姜丝、葱段、蒜粒、小米辣爆香。5.放入花甲，迅速翻炒。6.调入料酒、生抽，蒸鱼豉油。7.再放入豆瓣酱，翻炒均匀。8.淋入适量啤酒，大火翻炒至略略收汁。\r9.撒入香菜段，翻炒均匀，关火即可。', '/summerimages/img27.png', null, '2');
INSERT INTO `summermenu` VALUES ('28', '东坡肉', '五花肉', '蒸煮炒', '/summerimages/1585475704699.png', '3', '2');
